func TestAccSakuraInternet_basic(t *testing.T) {
	resourceName := "sakura_internet.foobar"
	rand := test.RandomName()

	var internet iaas.Internet
	resource.Test(t, resource.TestCase{
		PreCheck:                 func() { test.AccPreCheck(t) },
		ProtoV6ProviderFactories: test.AccProtoV6ProviderFactories,
		CheckDestroy: resource.ComposeTestCheckFunc(
			test.CheckSakuraDiskDestroy,
			test.CheckSakuraInternetDestroy,
			test.CheckSakuraIconDestroy,
			test.CheckSakuraServerDestroy,
		),
		Steps: []resource.TestStep{
			{
				Config: test.BuildConfigWithArgs(testAccSakuraInternet_basic, rand),
				Check: resource.ComposeTestCheckFunc(
					testCheckSakuraInternetExists(resourceName, &internet),
					resource.TestCheckResourceAttr(resourceName, "name", rand),
					resource.TestCheckResourceAttr(resourceName, "description", "description"),
					resource.TestCheckResourceAttr(resourceName, "tags.#", "2"),
					resource.TestCheckResourceAttr(resourceName, "tags.0", "tag1"),
					resource.TestCheckResourceAttr(resourceName, "tags.1", "tag2"),
					resource.TestCheckResourceAttr(resourceName, "netmask", "28"),
					resource.TestCheckResourceAttr(resourceName, "band_width", "100"),
					resource.TestCheckResourceAttr(resourceName, "server_ids.#", "0"),
					resource.TestCheckResourceAttr(resourceName, "ip_addresses.#", "11"),
					resource.TestCheckResourceAttrPair(
						resourceName, "icon_id",
						"sakura_icon.foobar", "id",
					),
				),
			},
			{
				Config: test.BuildConfigWithArgs(testAccSakuraInternet_update, rand),
				Check: resource.ComposeTestCheckFunc(
					testCheckSakuraInternetExists(resourceName, &internet),
					resource.TestCheckResourceAttr(resourceName, "name", rand+"-upd"),
					resource.TestCheckResourceAttr(resourceName, "description", "description-upd"),
					resource.TestCheckResourceAttr(resourceName, "tags.#", "2"),
					resource.TestCheckResourceAttr(resourceName, "tags.0", "tag1-upd"),
					resource.TestCheckResourceAttr(resourceName, "tags.1", "tag2-upd"),
					resource.TestCheckResourceAttr(resourceName, "netmask", "28"),
					resource.TestCheckResourceAttr(resourceName, "band_width", "500"),
					resource.TestCheckResourceAttr(resourceName, "server_ids.#", "0"),
					resource.TestCheckResourceAttr(resourceName, "ip_addresses.#", "11"),
					resource.TestCheckResourceAttr(resourceName, "enable_ipv6", "true"),
					resource.TestCheckResourceAttr(resourceName, "ipv6_prefix_len", "64"),
					resource.TestCheckNoResourceAttr(resourceName, "icon_id"),
				),
			},
		},
	})
}

func testCheckSakuraInternetExists(n string, internet *iaas.Internet) resource.TestCheckFunc {
	return func(s *terraform.State) error {
		rs, ok := s.RootModule().Resources[n]

		if !ok {
			return fmt.Errorf("not found: %s", n)
		}

		if rs.Primary.ID == "" {
			return errors.New("no Internet ID is set")
		}

		client := test.AccClientGetter()
		internetOp := iaas.NewInternetOp(client)
		zone := rs.Primary.Attributes["zone"]

		foundInternet, err := internetOp.Read(context.Background(), zone, common.SakuraCloudID(rs.Primary.ID))
		if err != nil {
			return err
		}

		if foundInternet.ID.String() != rs.Primary.ID {
			return fmt.Errorf("not found Internet: %s", rs.Primary.ID)
		}

		*internet = *foundInternet
		return nil
	}
}

