func TestAccImportSakuraInternet_basic(t *testing.T) {
	rand := test.RandomName()

	checkFn := func(s []*terraform.InstanceState) error {
		if len(s) != 1 {
			return fmt.Errorf("expected 1 state: %#v", s)
		}
		expects := map[string]string{
			"name":        rand,
			"netmask":     "28",
			"band_width":  "100",
			"enable_ipv6": "false",
			"description": "description",
			"tags.0":      "tag1",
			"tags.1":      "tag2",
		}

		if err := test.CompareStateMulti(s[0], expects); err != nil {
			return err
		}
		return test.StateNotEmptyMulti(s[0],
			"icon_id",
			"vswitch_id",
			"network_address",
			"gateway",
			"min_ip_address",
			"max_ip_address",
			"ip_addresses.0",
		)
	}

	resourceName := "sakura_internet.foobar"

	resource.Test(t, resource.TestCase{
		PreCheck:                 func() { test.AccPreCheck(t) },
		ProtoV6ProviderFactories: test.AccProtoV6ProviderFactories,
		CheckDestroy: resource.ComposeTestCheckFunc(
			test.CheckSakuraInternetDestroy,
			test.CheckSakuraIconDestroy,
		),
		Steps: []resource.TestStep{
			{
				Config: test.BuildConfigWithArgs(testAccSakuraInternet_basic, rand),
			},
			{
				ResourceName:      resourceName,
				ImportState:       true,
				ImportStateCheck:  checkFn,
				ImportStateVerify: true,
			},
		},
	})
}

var testAccSakuraInternet_basic = `
resource "sakura_internet" "foobar" {
  name        = "{{ .arg0 }}"
  icon_id     = sakura_icon.foobar.id
  description = "description"
  tags        = ["tag1", "tag2"]
}

resource "sakura_icon" "foobar" {
  name          = "{{ .arg0 }}"
  base64content = ""
}
`

var testAccSakuraInternet_update = `
data "sakura_archive" "ubuntu" {
  os_type = "ubuntu"
}

resource "sakura_disk" "foobar" {
  name              = "{{ .arg0 }}"
  source_archive_id = data.sakura_archive.ubuntu.id
}

resource "sakura_server" "foobar" {
  name        = "{{ .arg0 }}"
  disks       = [sakura_disk.foobar.id]
  network_interface = [{
    upstream = sakura_internet.foobar.vswitch_id
  }]
  disk_edit_parameter = {
    ip_address = sakura_internet.foobar.ip_addresses[0]
    gateway    = sakura_internet.foobar.gateway
    netmask    = sakura_internet.foobar.netmask
  }
}

resource "sakura_internet" "foobar" {
  name        = "{{ .arg0 }}-upd"
  band_width  = 500
  enable_ipv6 = true
  description = "description-upd"
  tags        = ["tag1-upd", "tag2-upd"]
}`
